mapper.select:
SELECT A.DECISION_DATE
  FROM DECISION_TABLE A
  JOIN AD_APPLY_TABLE B ON(B.DECISION_CODE = A.DECISION_CODE)
 WHERE B.USER_CODE = #{ userCode }
   AND B.AD_CODE = #{ adCode }

mapper.insert:
INSERT
  INTO QUESTION_BOARD
VALUES ( QUESTION_BOARD_SEQ.NEXTVAL
       , #{ boardTitle }
       , #{ boardContent }
       , 2
       , SYSDATE
       , NULL
       , 1
       , #{ userCode }
       )

		INSERT
		  INTO PICTURE_TABLE
		VALUES ( PIC_CODE_SEQ.NEXTVAL
		       , #{ pictureName }
			   , #{ pictureUrl }
               , SYSDATE
               , 'N'
               , #{ pictureNewName }
               , #{ pictureUtilPath }
		       )

INSERT
  INTO AD_APPLY_TABLE
VALUES ( AD_CODE_SEQ.NEXTVAL
       , #{ companyName }
       , SYSDATE
       , #{ companyNumber }
       , #{ companyPhone }
       , #{ companyEmail }
       , #{ adContent }
       , 'N'
       , 'N'
       , NULL
       , PIC_CODE_SEQ.CURRVAL
       , NULL
       , #{ userCode }
       , #{ adTypeCode }
       , #{ adTitle }
       , NULL
       , NULL
       , NULL
       , NULL
       , NULL
       , 1
       , #{ leaderName }
       , #{ companyLocation }
       , #{ industry }
       , #{ industryDetail }
       )

mapper.update:
UPDATE AD_APPLY_TABLE
   SET STATE_CODE = '4'
     , CANCEL_APPLY_DATE = SYSDATE
 WHERE AD_CODE = #{ adCode }
   AND USER_CODE = #{ userCode }
