mapper.select:
	    
	     SELECT COUNT(*)
	      FROM (SELECT MAX(USER_CODE) KEEP (DENSE_RANK LAST ORDER BY MESSAGE_DATE)
                FROM MESSAGE_TABLE
                WHERE USER_CODE1 = #{ userCode1 } 
                   OR USER_CODE = #{ userCode1 }
                GROUP BY USER_CODE)

mapper.insert:
INSERT
  INTO MESSAGE_TABLE
VALUES ( MESSAGE_CODE_SEQ.NEXTVAL
       , #{ messageContent }
       , SYSDATE
       , #{ userCode1 }
       , #{ userCode }
       , #{ sendUserNick }
       , #{ receiveUserNick }
       )
